import { useState } from "react";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";


function Soup () {

    const [toggle, setToggle] = useState('hide-hint');
    const history = useHistory()

    document.getElementById('textbox').addEventListener('keydown', function(e) {
        if (e.key == 'Tab') {
          e.preventDefault();
          var start = this.selectionStart;
          var end = this.selectionEnd;

          // set textarea value to: text before caret + tab + text after caret
          this.value = this.value.substring(0, start) +
            "\t" + this.value.substring(end);

          // put caret at right position again
          this.selectionStart =
            this.selectionEnd = start + 1;
        }
      });

    // let toggleDisplay = 'hide-hint';

    let defaultAnswer = `
    <html>
        <head>
        <head>
        <body>
        </body>
    </html>
    `;

    const [codeBlock, setCodeBlock] = useState(defaultAnswer)


    return (
        <div>
            <h1> Soup </h1>
            <h2>Add a header element with your favorite food!</h2>
            <textarea value={codeBlock} onChange={(e) => setCodeBlock(e.target.value)}></textarea>
            <h3 className = { toggle }>Does header have an opening and closing tag?</h3>
            <div className="pageButtons">
            <button onClick={() => toggle ? setToggle('') : setToggle('hide-hint')}>Hint</button>
            <button>View Solution</button>
            <button onClick={() => setCodeBlock(defaultAnswer)}>Reset</button>
            <button onClick={() => console.log(codeBlock)}>Submit</button>
            </div>       
            <button onClick={() => history.push('/')}>Home</button>
        </div>
    )
}

export default Soup;
