import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useHistory } from "react-router-dom";
import { useEffect } from 'react';
import './Details.css';

function Details() {

    const dispatch = useDispatch();
    const history = useHistory();
    const params = useParams();

    const genres = useSelector(store => store.genres);
    let currentMovie = genres[0];
    let movieId = params.movieId;

    useEffect(() => {
        dispatch({
            type: 'DETAIL_MOVIE',
            payload: movieId
        })
    }, []);

    if (genres.length === 0) {
        return (
            <>
                <h2>Loading ...</h2>
                <button onClick={() => history.push('/')}>Back</button>
            </>
        )
    }


    return (
        <>
            <h1>Details</h1>
            <div className='wrapper'>
                <div className='details'>
                    <h1 className='title'>{currentMovie.title}</h1>
                    {currentMovie.genre.map(genre => <h3 className='genre'>{genre} </h3>)}
                    <h3 className='description'>{currentMovie.description}</h3>
                    <img src={currentMovie.poster} className='image'></img>
                    <button onClick={() => history.push('/')}>Back</button>
                </div>
            </div>
        </>
    )
}

export default Details;
