import { useState } from "react";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";


function Soup () {

    const [toggle, setToggle] = useState('hide-hint');
    const history = useHistory()

    // let toggleDisplay = 'hide-hint';

    const [codeBlock, setCodeBlock] = useState(    `
    <html>
        <head>
        <head>
        <body>
        </body>
    </html>
    `)


    return (
        <div>
            <h1> Soup </h1>
            <h2>Add a header element with your favorite food!</h2>
            <textarea  onChange = {setCodeBlock((e) => e.target.value)} defaultValue={codeBlock}></textarea>
            <h3 className = { toggle }>Does header have an opening and closing tag?</h3>
            <div className="pageButtons">
            <button onClick={() => toggle ? setToggle('') : setToggle('hide-hint')}>Hint</button>
            <button>View Solution</button>
            <button onClick={() => console.log(codeBlock)}>Submit</button>
            </div>       
            <button onClick={() => history.push('/')}>Home</button>
        </div>
    )
}

export default Soup;
